                    sh = hLine._program;
                    sha = sh.attributes;
                    shu = sh.uniforms;
                    gl.uniform2fv(shu.viewport, [width, height]);
                    gl.uniform4fv(shu.extentParamsHigh, extentParamsHigh);
                    gl.uniform4fv(shu.extentParamsLow, extentParamsLow);
