        //this.geoObjectsTextureAtlas.clear()
        //this.fontAtlas.clear();
        this._entityCollections = [];
        this.handler.ONCANVASRESIZE = null;
        this.handler.destroy();
        // @ts-ignore
        this.handler = null;
